import {
    Component,
    HostBinding,
    ViewEncapsulation,
    Output,
    EventEmitter,
    OnInit
} from '@angular/core';

@Component({
    selector: 'cui-list-controller-clear-filters',
    templateUrl: 'list-controller-clear-filters.component.html',
    encapsulation: ViewEncapsulation.None
})
export class ListControllerClearFiltersComponent implements OnInit {
    @Output() onClearFilters: EventEmitter<string> = new EventEmitter<string>();
    @HostBinding('class.cui-list-controller-clear-filters')
    @HostBinding('class.cui-list-controller-component')
    addClass: boolean = true;
    _isOnClearFiltersUsed = false;

    constructor() {}

    ngOnInit() {
        this._isOnClearFiltersUsed = this.onClearFilters.observers.length > 0;
    }

    onTriggerClearFilters(): boolean {
        this.onClearFilters.emit();
        return false;
    }
}
