import { Component, HostBinding, Input, ViewEncapsulation } from '@angular/core';

@Component({
    selector: 'cui-list-view-label',
    templateUrl: 'list-view-label.component.html',
    encapsulation: ViewEncapsulation.None
})
export class ListViewLabelComponent {
    @HostBinding('class.cui-list-view-label') addClass: boolean = true;
    @HostBinding('class.multi-line') @Input() multiline: boolean = false;
    @HostBinding('style.width') get _width(): string {
        return this.width ? this.width + '%' : null;
    }
    @Input() width?: number;
    @Input() showTooltip: boolean = true;
}
