import { Component, HostBinding, Input, ViewEncapsulation, OnInit } from '@angular/core';

@Component({
    selector: 'cui-list-view-value',
    templateUrl: 'list-view-value.component.html',
    encapsulation: ViewEncapsulation.None
})
export class ListViewValueComponent implements OnInit {
    @HostBinding('class.cui-list-view-value') addClass: boolean = true;
    @HostBinding('class.form-field') @Input() formField: boolean = false;
    @HostBinding('class.multi-line') @Input() multiline: boolean = false;
    @Input() showTooltip: boolean = true;

    ngOnInit() {
        if (this.formField) {
            this.multiline = true;
            this.showTooltip = false;
        }
    }
}
