import { style, animate, transition, state, trigger, keyframes } from '@angular/animations';

export const LOADER: { LOADER_ANIMATION: any[] } = {
    LOADER_ANIMATION: [
        trigger('visibility', [
            state('visible-fixed-height', style({opacity: 1, left: '0px'})),
            state('visible', style({opacity: 1, height: '*', left: '0px'})),
            state('none', style({opacity: 0, left: '-99999px'})),
            transition('none => *', style({left: '0px', opacity: 1})),
            transition('visible => none', [
                animate('500ms', keyframes([
                    style({offset: 0, opacity: 1}),
                    style({offset: 0.9, opacity: 0.1}),
                    style({left: '-99999px', opacity: 0, offset: 1.0})
                ]))
            ]),
            transition('visible-fixed-height => none', [
                animate('500ms', keyframes([
                    style({offset: 0, opacity: 1}),
                    style({offset: 0.9, opacity: 0.1}),
                    style({left: '-99999px', opacity: 0, offset: 1.0})
                ]))
            ])
        ])
    ]
};
