import { ViewContainerRef, Injectable } from '@angular/core';

@Injectable()
export class LoaderService {
    private _loaderContainerRef: ViewContainerRef;

    constructor() {}

    /**
     * Sets the loader container ref
     * @param ref
     */
    set containerRef(ref: ViewContainerRef) {
        this._loaderContainerRef = ref;
    }

    /**
     * Returns the loader container ref
     * @return {ViewContainerRef}
     */
    get containerRef(): ViewContainerRef {
        if (this._loaderContainerRef === undefined) {
            throw new Error(`
                Loader container ref has not been declared. Please set loader container ref
                in your root component. Read documentation for Loader Service for more info
            `);
        }
        return this._loaderContainerRef;
    }
}
