import { DIALOG_TYPE } from './dialog.constants';
import { QuickAssign } from '../../../core/helpers/quickassign.model';
import { ModalActionItem } from '../../../core/models/button-action-item.model';
import { TemplateRef } from '@angular/core';

// Configuration model for dialog, sets title, message actions and type. Should be provided on dialog creation.
// Title of the dialog is pure text. ie: 'Alert'
// Message in html. ei. <p>Delete this production specification</p>
export class DialogConfig extends QuickAssign {
    type: DIALOG_TYPE;
    title: string;
    message: HTMLElement;
    component?: any;
    width: string;
    template: TemplateRef<any>;
    actions: ModalActionItem[];
}
