import { NgModule, ModuleWithProviders } from '@angular/core';
import { ExternalTaskComponent } from './external-task.component';
import { BlockModule } from '../../block/block.module';
import { ContainerModule } from '../../container/container.module';
import { LoaderModule } from '../../loader/loader.module';

@NgModule({
    imports: [BlockModule, ContainerModule, LoaderModule],
    exports: [ExternalTaskComponent],
    declarations: [ExternalTaskComponent],
    entryComponents: [ExternalTaskComponent]
})

export class ExternalTaskModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: ExternalTaskModule
        };
    }
}
