import { NgModule, ModuleWithProviders } from '@angular/core';
import { ModalService } from './modal.service';
import { ModalHelperService } from './modal-helper.service';
import { DialogModule } from './dialog/dialog.module';
import { ProgressModalModule } from './progress-modal/progress-modal.module';
import { OverlayHelperService } from '../overlay';
import { DrawerModule } from '../drawer';

@NgModule({
    imports: [ DialogModule, ProgressModalModule, DrawerModule ]

})

export class ModalModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: ModalModule,
            providers: [ ModalService, ModalHelperService, OverlayHelperService ]
        };
    }
}
