import { Input, Component, OnDestroy } from '@angular/core';
import { Subscription } from 'rxjs';

@Component({
    selector: 'oden-issue-base-component',
    template: ``
 })
export class OdenIssueBaseComponent implements OnDestroy {
    protected _subscriptions: Array<Subscription> = [];
    @Input() show: boolean = false;

    ngOnDestroy(): void {
        this._subscriptions.forEach((subscrition) => subscrition.unsubscribe());
    }
}
