import { Directive, HostBinding, Input } from '@angular/core';
import { OdenTextInputBaseDirective } from '../oden-form-base-classes/oden-text-input-base-directive';

@Directive({
    selector: '[oden-input]',
})
export class OdenInputDirective extends OdenTextInputBaseDirective {
    @HostBinding('class.oden-input') _hasOdenInputClass: boolean = true;
    @HostBinding('class.locked-state') @Input() lockedState: boolean = false;
}
