import { CommonModule } from '@angular/common';
import { ModuleWithProviders, NgModule } from '@angular/core';

import { ContainerModule } from '../container/container.module';
import { TabsModule } from '../tabs/tabs.module';
import { PaneService } from './pane.service';
import { TaskPaneComponent } from './task-pane/task-pane.component';
import { StickyModule } from '../sticky/sticky.module';

@NgModule({
    imports: [CommonModule, ContainerModule, TabsModule, StickyModule],
    exports: [TaskPaneComponent],
    declarations: [TaskPaneComponent],
    entryComponents: [TaskPaneComponent]
})
export class PaneModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: PaneModule,
            providers: [PaneService]
        };
    }
}
