import { CommonModule } from '@angular/common';
import { ModuleWithProviders, NgModule } from '@angular/core';

import { PanelsComponent } from './panels.component';
import { PanelItemComponent } from './panel-item/panel-item.component';
import { LoaderModule } from '../loader';
import { IconModule } from '../icon';
import { TooltipModule } from '../tooltip';

@NgModule({
    imports: [CommonModule, LoaderModule, IconModule, TooltipModule],
    exports: [PanelsComponent, PanelItemComponent],
    declarations: [PanelsComponent, PanelItemComponent]
})

export class PanelsModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: PanelsModule
        };
    }
}

