import { CommonModule } from '@angular/common';
import { NgModule } from '@angular/core';
import { PreventDraggingDirective } from './prevent-dragging.directive';

@NgModule({
    imports: [CommonModule],
    exports: [PreventDraggingDirective],
    declarations: [PreventDraggingDirective]
})

export class PreventDraggingModule {
    static forRoot() {
        return {
            ngModule: PreventDraggingModule
        };
    }
}
