import { D3Text } from './types';

export class RelationGraphComponentUtil {
    static trim(d3Text: D3Text, maxWidth: number): void {
        let text = d3Text.text();
        while (d3Text.node().getComputedTextLength() > maxWidth && text.length) {
            text = text.slice(0, -1);
            d3Text.text(text + 'â€¦');
        }
    }
}
