import { CommonModule } from '@angular/common';
import { NgModule, ModuleWithProviders } from '@angular/core';
import { RelationGraphComponent } from './relation-graph.component';
import { FormModule } from '../form/form.module';
import { FormsModule } from '@angular/forms';

@NgModule({
    imports: [CommonModule, FormModule, FormsModule],
    exports: [RelationGraphComponent],
    declarations: [RelationGraphComponent]
})

export class RelationGraphModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: RelationGraphModule
        };
    }
}
