import { style, animate, transition, state, trigger } from '@angular/animations';

export const SNACK_BAR: { SNACK_BAR_ANIMATION: any[] } = {
    SNACK_BAR_ANIMATION: [
        trigger('visibility', [
            state('visible', style({opacity: 1, transform: 'translate(-50%,0px)'})),
            state('hidden', style({opacity: 0, transform: 'translate(-50%,60px)'})),
            transition('hidden => visible', [
                style({opacity: 0, transform: 'translate(-50%,60px)'}),
                animate('300ms')
            ]),
            transition('* => void', animate('300ms', style({opacity: 0, transform: 'translate(-50%,60px)'})))
        ])
    ]
};
