import { Component, Input, Output, EventEmitter, ViewEncapsulation, HostBinding } from '@angular/core';
import { ProfileModel, ActionItem } from '../../../core/models';
import { StringHelper } from '../../../core/helpers/string.helper';

@Component({
    selector: 'cui-profile-display',
    templateUrl: './profile-display.component.html',
    styleUrls: ['./profile-display.component.scss'],
    encapsulation: ViewEncapsulation.None
})
export class ProfileDisplayComponent {
    @HostBinding('class.cui-profile-display') addClass: boolean = true;
    @Input() profile: ProfileModel;
    @Input() actions: Array<ActionItem> = [];
    @Output() onActionClick: EventEmitter<ActionItem> = new EventEmitter<ActionItem>();
    constructor() { }

    actionClick(action: ActionItem): void {
        this.onActionClick.emit(action);
    }

    convertNameToInitials(name: string): string {
        return StringHelper.stringToInitials(name);
    }
}

