import { animate, state, style, transition, trigger } from '@angular/animations';
import { ANIMATION } from '../../core/constants';

export const TAB_VIEW: { ANIMATION: any, ANIMATION_FULL: any } = {
    ANIMATION: trigger('routeAnimation', [
        state('*', style({opacity: 1, display: 'block'})),

        transition('void => moveLeft', [
            style({left: '-30%', opacity: 0}),
            animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT)
        ]),
        transition('void => moveRight', [
            style({left: '30%', opacity: 0}),
            animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT)
        ]),
        transition('void => moveDown', [
            style({transform: 'translateY(100px)', opacity: 0}),
            animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT)
        ]),

        transition('moveLeft => void', animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT, style({left: '-30%', opacity: 0}))),
        transition('moveRight => void', animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT, style({left: '30%', opacity: 0}))),
        transition('moveDown => void', animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT,
            style({transform: 'translateY(100px)', opacity: 0}))),
    ]),
    ANIMATION_FULL: trigger('routeAnimation', [
        state('*', style({opacity: 1, display: 'block'})),

        transition('void => moveLeft', [
            style({left: '-130%'}),
            animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT)
        ]),
        transition('void => moveRight', [
            style({left: '130%'}),
            animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT)
        ]),
        transition('void => moveDown', [
            style({transform: 'translateY(100px)'}),
            animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT)
        ]),

        transition('moveLeft => void', animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT, style({left: '-130%'}))),
        transition('moveRight => void', animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT, style({left: '130%'}))),
        transition('moveDown => void', animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT, style({transform: 'translateY(100px)'})))
    ])
};
