// tslint:disable:component-selector-name component-selector-type
import { Component, Input, ViewEncapsulation } from '@angular/core';
import { FIELD_TYPE } from '../../../../../core/constants/field-type.constants';

@Component({
    selector: 'cui-table-cell-date',
    templateUrl: 'table-cell-date.component.html',
    encapsulation: ViewEncapsulation.None
})

export class TableCellDateComponent {
    @Input() data: any;
    @Input() type: FIELD_TYPE;

    constructor() {
    }

    getDateExpression(): string {
        if (this.type === FIELD_TYPE.DATE) {
            return 'y-MM-dd';
        } else {
            return 'y-MM-dd HH:mm:ss';
        }
    }
}
