import { Component, Input, OnInit, ViewEncapsulation } from '@angular/core';
import { TableCellEditComponent } from '../table-cell-edit.component';
import { TABLE_CELL_EDIT } from '../table-cell-edit.animations';

@Component({
    selector: 'cui-table-cell-edit-date',
    templateUrl: './table-cell-edit-date.component.html',
    encapsulation: ViewEncapsulation.None,
    animations: [ TABLE_CELL_EDIT.ANIMATION ]
})

export class TableCellEditDateComponent extends TableCellEditComponent implements OnInit {
    @Input() editData: Date = null;
    @Input() editOptions: {showPicker?: boolean};

    ngOnInit() {
        this.editOptions = this.editOptions || {showPicker: true};
    }
}
