import { Component, Input, OnInit, ViewEncapsulation } from '@angular/core';
import { TableCellEditComponent } from '../table-cell-edit.component';
import { TABLE_CELL_EDIT } from '../table-cell-edit.animations';

@Component({
    selector: 'cui-table-cell-edit-time',
    templateUrl: './table-cell-edit-time.component.html',
    encapsulation: ViewEncapsulation.None,
    animations: [TABLE_CELL_EDIT.ANIMATION]
})

export class TableCellEditTimeComponent extends TableCellEditComponent implements OnInit {
    @Input() editData: string = null;
    @Input() editOptions: { showSeconds?: boolean, showPicker?: boolean } = {};
    ngOnInit() {
        this.editOptions = this.editOptions || { showSeconds: false, showPicker: true };
    }
}
