import { CommonModule } from '@angular/common';
import { NgModule, ModuleWithProviders } from '@angular/core';
import { RouterModule } from '@angular/router';
import { TabsComponent } from './tabs.component';
import { InkBarComponent } from './ink-bar/inkbar.component';
import { TabViewService } from '../tab-view/tab-view.service';
import { IconModule } from '../icon/icon.module';
import { FormModule } from '../form/form.module';

@NgModule({
    imports: [CommonModule, RouterModule, IconModule, FormModule],
    exports: [TabsComponent, InkBarComponent],
    declarations: [TabsComponent, InkBarComponent]
})

export class TabsModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: TabsModule,
            providers: [TabViewService]
        };
    }
}
