import { EventEmitter, Injectable } from '@angular/core';

@Injectable()
export class TileGroupService {
    public onSelectTile: EventEmitter<string> = new EventEmitter<string>();
    public onFocusTile: EventEmitter<string> = new EventEmitter<string>();
    public _selected: string;
    public _focused: string;

    constructor() { }

    get selected(): string {
        return this._selected;
    }
    set selected(id: string) {
        this._selected = id;
        if (id) {
            this.onSelectTile.emit(id);
        }
    }
    get focused(): string {
        return this._focused;
    }
    set focused(id: string) {
        this._focused = id;
        if (id) {
            this.onFocusTile.emit(id);
        }
    }
}
