import { ModuleWithProviders, NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { TogglerExpandComponent } from './toggler-expand/toggler-expand.component';
import { TogglerCollapseComponent } from './toggler-collapse/toggler-collapse.component';
import { IconModule } from './../icon/icon.module';
import { TogglerBreakPointDirective } from './toggler-breakpoint.directive';
import { TogglerComponent } from './toggler.component';

@NgModule({
    imports: [CommonModule, IconModule],
    exports: [TogglerComponent, TogglerBreakPointDirective, TogglerCollapseComponent, TogglerExpandComponent],
    declarations: [TogglerComponent, TogglerBreakPointDirective, TogglerCollapseComponent, TogglerExpandComponent]
})

export class TogglerModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: TogglerModule
        };
    }
}
