import { Component, Input, TemplateRef, ElementRef, ViewEncapsulation, HostBinding } from '@angular/core';

@Component({
    selector: 'cui-tooltip-template',
    templateUrl: 'tooltip-template.component.html',
    styleUrls: ['tooltip-template.component.scss'],
    encapsulation: ViewEncapsulation.None
})
export class TooltipTemplateComponent {
    @HostBinding('class.cui-tooltip-template') addClass: boolean = true;
    @Input() template: TemplateRef<any>;
    @Input() context: any;

    constructor(private _elementRef: ElementRef) {}

    getElement(): HTMLElement {
        return this._elementRef.nativeElement;
    }
}
