import { HostBinding, Input, OnChanges, OnDestroy } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { TabView, TabViewService } from '../../tab-view';
import { UnSub } from '../../../core/classes/subscription.decorator';

@UnSub()
export class UniversalTabView extends TabView implements OnChanges, OnDestroy {
    @HostBinding('@routeAnimation') animationDirection: string;
    @HostBinding('class.hidden') get isHidden(): boolean {
        return !this.visible;
    }
    @HostBinding('class.universal-tab-content') @HostBinding('class.tab-view') tabviewClasses: boolean = true;
    // tslint:disable-next-line:no-input-rename
    @Input('tabIndex') tabIndex: number;
    public visible: boolean = false;

    constructor(tabViewService: TabViewService, activatedRoute: ActivatedRoute) {
        super(tabViewService, activatedRoute);
    }

}
