import { state, style, transition, animate, trigger } from '@angular/animations';
import { ANIMATION } from './animation.constants';
import { SIZING } from './sizing.constants';

export const FOCUS_SHIFT: { ANIMATION: any, ANIMATION_FULL: any } = {
    ANIMATION: trigger('shiftAnimation', [
        state('*', style({top: SIZING.SYSTEMBAR_HEIGHT + 'px'})),
        state('void', style({top: '100px', opacity: 0})),
        transition('void => *', [
            animate('500ms ' + ANIMATION.EASE_IN_OUT_QUINT)
        ]),
        transition('* => void', [
            animate('300ms ' + ANIMATION.EASE_IN_OUT_QUINT)
        ])
    ]),
    ANIMATION_FULL: trigger('shiftAnimation', [
        state('*', style({display: 'block'})),
        transition('void => *', [
            style({opacity: 0}),
            animate('400ms ' + ANIMATION.EASE_OUT_QUAD)
        ])
    ])
};
