export const LINECHARTCONFIG: LineChartConfig = {
    /**
     *  Cofiguration for line chart.
     *  MARGINS: sets the inner margins of the SVG element
     *  MAXLINES: the maximum amount of models to display as lines
     *  DURATION: transition duration
     */
    MARGINS: { top: 30, right: 50, bottom: 20, left: 40 },
    MAXLINES: 12,
    DURATION: 600
};

export type LineChartMargins = {
    top: number,
    right: number,
    bottom: number,
    left: number,
};

export type LineChartConfig = {
    MARGINS: LineChartMargins,
    MAXLINES: number,
    DURATION: number,
};
