export class DraggableHelper {
    /**
     * Determine if element is draggable.
     * @param {Element} element
     * @param {string} draggableClass
     */
    static isDraggableElement(element: Element, draggableClass: string): boolean {
        if (!element) {
            return false;
        } else if (element.classList.contains(draggableClass)) {
            return true;
        } else if (element.classList.contains('cui-prevent-dragging')) {
            return false;
        } else if (element.parentElement && element.parentElement !== document.documentElement) {
            return DraggableHelper.isDraggableElement(element.parentElement, draggableClass);
        } else {
            return false;
        }
    }
}
