import { Injectable } from '@angular/core';
import { Observable, ReplaySubject } from 'rxjs';
import { WHITELABELTESTCOLOR } from '../../constants/whitelabel-test.constants';

@Injectable()
export class WhitelabelServiceStub {
    private _whitelabelingLoaded$: ReplaySubject<null> = new ReplaySubject<
        null
    >();
    _whitelabelingLoaded: Observable<
        null
    > = this._whitelabelingLoaded$.asObservable();

    constructor() {
        this._whitelabelingLoaded.subscribe();
    }

    setWhitelabeling(): void {
        this._whitelabelingLoaded$.next(null);
    }

    getPropertyValue(_searchString: string): string {
        return WHITELABELTESTCOLOR.BRAND_PRIMARY;
    }

    getBrandColors() {
        return [WHITELABELTESTCOLOR.BRAND_PRIMARY, WHITELABELTESTCOLOR.BRAND_SECONDARY,
            WHITELABELTESTCOLOR.BRAND_TETRIARY, WHITELABELTESTCOLOR.BRAND_QUATERNARY];
    }

}
