import { ActionItem } from '../models';

export interface ITableRowData {
    hidden?: boolean;
    selected?: boolean;
    multiRow?: boolean;
    multiRowWrap?: boolean;
    /** @deprecated - use Templates instead! */
    focus?: boolean;
    /** @deprecated - use Templates instead! */
    path?: string;
    uuid?: string;
    /** @deprecated - use Templates instead! */
    edit?: boolean;
    singleActions?: boolean;
    rowActions?: Array<ActionItem>;
    /** @deprecated - use Templates instead! */
    editError?: {}; // For Example editErrors['name'] = 'Can't be blank';
    /** @deprecated - use Templates instead! */
    editDisabled?: Array<string>; // For Example editDisabled = ['name', 'id'];
    /** @deprecated - use Templates instead! */
    editedFields?: {}; // For example editedFields['name'] = 'value';
    externalApp?: string; // Name of external application. Should be the same for all links of the same ext app.
}
