import { BreadcrumbItemInterface } from '../interfaces/breadcrumb.interface';

export class BreadcrumbItem implements BreadcrumbItemInterface {
    id: number;
    title: string;
    path?: string;
    clickable: boolean = true;
    hidden?: boolean = false;

    constructor(obj: {
        id?: number;
        title?: string;
        path?: string;
        clickable?: boolean;
        hidden?: boolean;
    } = {}) {
        Object.assign(this, obj);
    }
}
