import { CharacteristicValueType } from '../constants';

export class CharacteristicValue {
    id: string;
    value?: any;
    humanReadableId?: string;
    valueType: CharacteristicValueType;
    valueFrom?: any;
    valueTo?: any;
    isDefault?: boolean;
    isSelected?: boolean;
    displayValue?: string;
    name?: string;
    unitOfMeasure?: string;

    constructor(obj: Partial<CharacteristicValue> = {}) {
        Object.assign(this, obj);
    }
}


