import { CharacteristicValue } from './characteristic-value.model';
import { CharacteristicProperty } from './characteristic-property.model';
import { CharacteristicValueType } from '../constants';

export class Characteristic {
    id: string;
    name: string;
    description?: string;
    humanReadableId?: string;
    minCardinality: number;
    maxCardinality: number;
    characteristicValues?: Array<CharacteristicValue>;
    value?: string;
    unitOfMeasure?: string;
    valueFrom?: any;
    valueTo?: any;
    displayValue?: string;
    valueType: CharacteristicValueType;
    properties?: Array<CharacteristicProperty>;
    [x: string]: any;
    constructor(obj: Partial<Characteristic> = {}) {
        Object.assign(this, obj);
    }

    public isMultiValued(): boolean {
        return (this.minCardinality >= 0 && this.maxCardinality > 1);
    }

}
