import { IElementOverlayConfig } from '../interfaces/element-overlay-config.interface';
import { ELEMENT_OVERLAY_STATE } from '../constants/element-overlay-state.constants';
import { ActionItem } from './action-item.model';
import { TemplateRef } from '@angular/core';

export class ElementOverlayConfig implements IElementOverlayConfig {
    state: ELEMENT_OVERLAY_STATE = ELEMENT_OVERLAY_STATE.NONE;
    label?: string;
    text?: string;
    textTemplate?: TemplateRef<any>;
    actions?: Array<ActionItem>;
    inlinePlaceholder?: boolean = false;
    constructor(obj?: Partial<IElementOverlayConfig>) {
        Object.assign(this, obj);
    }
}
