import { Observable } from 'rxjs/Observable';
import { ALIGNMENT } from '../constants';
import { EntityVersion } from './entity-version.model';

export class GroupTitleAttribute {
    label: string | Observable<string>; // The subtitle label to display in group title attribute
    icon: string; // Icon for subtitle in group title
    iconAlignment: ALIGNMENT; // Alignment of icon in subtitle. It should be Left or Right alignment.
    stateBatch: EntityVersion; // To StateBadge as a subtitle

    constructor(obj: {
        label?: string | Observable<string>,
        icon?: string,
        iconAlignment?: ALIGNMENT,
        stateBatch?: EntityVersion,
    } = {}) {
        Object.assign(this, obj);
    }
}
