export class ListControllerCategoryFilterItem {
    field: string; // Used as an identifier in the callback
    label: string; // Displayed in the list
    selected?: boolean = true;
    disabled?: boolean = false;
    tooltip?: string;

    constructor(obj: {
        field?: string;
        label?: string;
        selected?: boolean;
        disabled?: boolean;
        tooltip?: string;
    } = {}) {
        Object.assign(this, obj);
    }
}
