import { DATE_TYPE } from '../constants/date.constants';

export class ListControllerDateFilter {
    label: string;
    field?: string;
    type: DATE_TYPE;
    value?: Date | string;

    constructor(obj: {
        label?: string;
        field?: string;
        type?: DATE_TYPE;
        value?: Date | string;
    } = {}) {
        Object.assign(this, obj);
    }
}
