import { TAGTYPE } from '../constants/tags.constants';
import { ITag } from '../interfaces/tag.interface';

export class Tag implements ITag {
    type: TAGTYPE = TAGTYPE.Default;
    value: string;
    // description deprecated
    description?: string;
    label?: string;
    url?: string;
    bold?: boolean;
    tooltip: string;
    constructor(tag: Partial<ITag>) {
        this.value = tag.value;
        this.url = tag.url;
        this.bold = tag.bold;
        this.description = tag.description;
        this.label = tag.label;
        this.type = tag.type || TAGTYPE.Default;
        this.tooltip = tag.tooltip || '';
    }
}
