import { TileSection } from './tile-section.model';
import { STATE } from '../constants';
import { UUID } from '../helpers/uuid.class';
import { EntityVersion } from './entity-version.model';

export class TileData {
    uuid?: string = `tile-${UUID.create()}`;
    id?: string;
    label: string;
    subtitle: string;
    subtype: string;
    sections: Array<TileSection> = [];
    state: STATE;
    text: string;
    versions: number | Array<EntityVersion> = 0;
    activeDate: string | Date;
    modifiedDate?: Date;
    dateFormat: string = 'll';
    path?: string;
    versionsTooltip?: string;
    icon?: string;
    iconTooltip?: string;
    selected?: boolean = false;
    image?: string;
    discontinued?: boolean = false;
    entityRelationType?: string;
    constructor(obj: Partial<TileData> = {}) {
        Object.assign(this, obj);
    }
}
