export enum ValidationSummaryItemType {
    ERROR = 'error',
    WARNING = 'warning'
}

export class ValidationSummaryItem {
    type: ValidationSummaryItemType | string;
    message: string;
    data?: any;
    goToPath?: string;
    goTo?: boolean;

    constructor(obj: {
        type: ValidationSummaryItemType | string,
        message: string,
        data?: any,
        goToPath?: string,
        goTo?: boolean
    }) {
        Object.assign(this, obj);
    }
}

export class ValidationSummaryItemGroup {
    type: ValidationSummaryItemType | string;
    title: string;
    icon: string;
    color: string;

    constructor(obj: {
        type: ValidationSummaryItemType | string;
        title: string;
        icon: string;
        color: string;
    }) {
        Object.assign(this, obj);
    }
}
