import { DecimalPipe } from '@angular/common';
import { Pipe, PipeTransform } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';

@Pipe({
    name: 'cuiNumber'
})
export class CuiDecimalPipe implements PipeTransform {
    constructor(private _translateService: TranslateService) {}

    public transform(value: any, digits: string = null): any {
        const ngPipe = new DecimalPipe(this._translateService.currentLang);
        return ngPipe.transform(value, digits);
    }
}
