import { CommonModule } from '@angular/common';
import { ModuleWithProviders, NgModule } from '@angular/core';
import { InstanceOfPipe } from './instance-of.pipe';
import { CuiDecimalPipe } from './cui-decimal.pipe';
import { CuiHighlightPipe } from './cui-highlight.pipe';

@NgModule({
    imports: [ CommonModule ],
    exports: [ InstanceOfPipe, CuiDecimalPipe, CuiHighlightPipe ],
    declarations: [ InstanceOfPipe, CuiDecimalPipe, CuiHighlightPipe ],
    providers: [ CuiDecimalPipe, CuiHighlightPipe ]
})

export class PipesModule {
    static forRoot(): ModuleWithProviders {
        return {
            ngModule: PipesModule
        };
    }
}
