import { ProfileModel } from '../models/profile.model';
import { Observable } from 'rxjs';
import { AuthRequest } from '../models/auth-request.model';
import { AuthResponse } from '../models/auth-response.model';

/**
 * `UserService` is responsible for getting the user profile and
 * auhtorization for the resources
 */
export abstract class UserService {

 /**
  * get authorization info
  */
  abstract can(authRequest: AuthRequest | AuthRequest[]): Observable<boolean | AuthResponse[]>;

 /**
  * get profile info.
  */
  abstract getProfile(): ProfileModel;
}
